sect1.programlisting:
IMPORT STD;
  INTEGER2  MyYear  := 1963;  
  UNSIGNED1 MyMonth := 11;   
  UNSIGNED1 MyDay   := 22;
  INTEGER1  MyHour   := 13;
  UNSIGNED1 MyMinute := 30;
  UNSIGNED1 MySecond := 02;
  BOOLEAN   is_local_time := TRUE;
S := STD.Date.SecondsFromParts(MyYear, MyMonth, MyDay, MyHour, MyMinute, MySecond);
